import { useState } from 'react'

function MyButton () {
  const [buttonText, setButtonText] = useState('Pulsame')
  function HandleClick () {
    const [count, setCount] = useState(10)
    if (count !== 0) {
      setCount(count - 1)
      setButtonText('Te quedan ' + count + ' pulsaciones!')
    } else {
      setButtonText('Has pulsado el botÃ³n el mÃ¡ximo nÃºmero de veces...ðŸ˜¡')
    }

    // alert('Â¡Me hiciste clic! Primer componente creado ðŸ˜š!')
  }

  return (
    <button
      onClick={HandleClick}
      style={{
        backgroundColor: count === -1 ? 'red' : 'green'
      }}
    >
      {buttonText}
    </button>
  )
}
export default MyButton
