    if (count > 0) {
      setCount(count - 1)
    } else {
      setButtonText('Has pulsado el botÃ³n el mÃ¡ximo permitido de veces... ðŸ˜¡')
    }

    // alert('Â¡Me hiciste clic! Primer componente creado ðŸ˜š!')
  }

  return (
    <button
      onClick={HandleClick}
      style={{
        backgroundColor: count > 0 ? 'green' : 'red'
      }}
    >
      {buttonText}
    </button>
  )
}
