import { useState } from 'react'

function MyButton () {
  const [count, setCount] = useState(10)
  let buttonText
  function HandleClick () {
    if (count > 0) {
      setCount(count - 1)
      buttonText = 'Has clickado el botón' + count + 'veces!'
    } else {
      buttonText = 'Has pulsado el botón el máximo permitido de veces... 😡'
    }

    // alert('¡Me hiciste clic! Primer componente creado 😚!')
  }

  return (
    <button
      onClick={HandleClick}
      style={{
        backgroundColor: count > 0 ? 'green' : 'red'
      }}
    >
      {buttonText}
    </button>
  )
}
export default MyButton
